mapper.select:
SELECT distinct_user.role_uid, COUNT(distinct_user.user_uid) user_count FROM (SELECT DISTINCT role_uid, user_uid FROM tnt_biz_erp_user_role WHERE archive = 1 AND role_uid IN
#{roleUid}
) distinct_user GROUP BY distinct_user.role_uid

mapper.delete:
DELETE FROM 
     tnt_biz_erp_user_role 
WHERE 
     role_uid = #{userRoleDTO.roleUid} 

         AND user_uid in 
         #{userUid}


         AND mall_uid in 
         #{mallUid}

mapper.select:
 SELECT
    count(id)
FROM
    tnt_biz_erp_user_role
WHERE
    archive = 1

  AND role_uid = #{roleUid} 


  AND mall_uid = #{mallUid}


  AND user_uid = #{userUid}

SELECT
  role.role_uid, account.`name`, account.phone, account.login_name,
  account.gender, account.status, account.user_uid
FROM
  tnt_biz_erp_user_role role
INNER JOIN
  tnt_biz_erp_user_account account
ON role.user_uid = account.user_uid AND role.role_uid = #{roleUid}

    AND account.login_name = #{loginName}

 AND role.archive =1
GROUP BY account.user_uid

    ORDER BY role.${sortKey} ${sortOrder}


    LIMIT #{pageOffset}, #{pageSize}

SELECT COUNT(DISTINCT(account.user_uid)) FROM tnt_biz_erp_user_role role INNER JOIN tnt_biz_erp_user_account account ON role.user_uid = account.user_uid AND role.role_uid = #{roleUid}
AND account.login_name = #{loginName}
AND role.archive =1
