mapper.select:
	   SELECT

		FROM
		    tnt_biz_erp_role
		WHERE
            archive = 1

               AND role_name like CONCAT('%' ,#{roleName}, '%')


               AND is_changeable = #{isChangeable}



            ORDER BY ${sortKey} ${sortOrder}

	    LIMIT #{pageOffset},#{pageSize}

SELECT COUNT(0) FROM tnt_biz_erp_role WHERE archive = 1
AND role_name like CONCAT('%' ,#{roleName}, '%')
AND is_changeable = #{isChangeable}

SELECT
FROM tnt_biz_erp_role WHERE archive = 1
AND role_uid = #{roleUid}
AND role_key = #{roleKey}

SELECT
FROM tnt_biz_erp_role WHERE archive = 1
AND role_key = #{roleKey}

mapper.insert:
INSERT INTO tnt_biz_erp_role (role_uid, role_name, role_desc, role_key,
is_changeable,
created_at, updated_at, created_by,
archive
) values (tnt_uuid_short(), #{roleName,jdbcType=VARCHAR}, #{roleDesc,jdbcType=VARCHAR}, #{roleKey,jdbcType=VARCHAR},
#{isChangeable},
now(), now(), #{createdBy,jdbcType=BIGINT},
#{archive}
)

mapper.select:
SELECT count(id)
FROM tnt_biz_erp_role
